<style>    .table .el-radio__label {        display: none !important;    }    .app-select-store .el-input input {        border-color: #DCDFE6 !important;    }    .el-input.el-input--small.el-input-group.el-input-group--append.el-input--suffix > span {        width: 40px !important;        overflow: hidden !important;    }</style><template id="app-select-store">    <div class="app-select-store">        <el-dialog title="选择门店" :visible.sync="storeDialog" top="10vh" width="550" append-to-body>            <div class="input-item" style="margin-bottom: 12px">                <el-input @keyup.enter.native="searchStore" size="small" placeholder="请输入门店名称"                          v-model="search.keyword" clearable                          @clear="searchStore">                    <el-button slot="append" icon="el-icon-search" @click="searchStore"></el-button>                </el-input>            </div>            <el-table class="table" v-loading="listLoading" :data="storeList" border>                <el-table-column prop="id" width="55">                    <template slot-scope="scope">                        <el-radio v-model="sIndex" :label="scope.$index"></el-radio>                    </template>                </el-table-column>                <el-table-column prop="name" label="门店名称"></el-table-column>            </el-table>            <!--工具条 批量操作和分页-->            <div flex="dir:left cross:center main:justify box:justify">                <el-pagination                        background                        layout="prev, pager, next, jumper"                        @current-change="pageChange"                        :page-size="Number(pagination.pageSize)"                        :total="pagination.total_count"                        style="text-align:left;margin: 15px 0"                        v-if="pagination">                </el-pagination>                <div slot="footer" class="dialog-footer">                    <el-button v-if="false" size="small" @click="storeDialog = false">取 消</el-button>                    <el-button size="small" type="primary" @click="storeConfirm">确 定</el-button>                </div>            </div>        </el-dialog>        <span @click="openstoreDialog">            <slot></slot>        </span>    </div></template><script>    Vue.component('app-select-store', {        template: '#app-select-store',        props: {            value: {                type: String,                default: () => {                    return ""                }            },        },        data() {            return {                sIndex: '',                listLoading: false,                search: {                    keyword: ''                },                storeList: null,                storeDialog: false,                page: 1,                pagination: null,            }        },        methods: {            openstoreDialog() {                //if (!this.storeList || !this.storeList.length) {                this.getList();                //}                this.storeDialog = true;            },            closestoreDialog() {                this.storeDialog = false;            },            storeConfirm() {                if (this.sIndex === '') {                    this.$emit('change', '');                    this.$emit('input', '');                } else {                    this.$emit('change', this.storeList[this.sIndex]);                    this.$emit('input', this.storeList[this.sIndex]['id']);                }                this.closestoreDialog();            },            pageChange(page) {                this.sIndex = '';                this.page = page;                this.getList();            },            searchStore() {                this.sIndex = '';                this.page = 1;                this.getList();            },            getList() {                this.listLoading = true;                request({                    params: Object.assign({}, {                        r: 'mall/store/index',                        page_size: 10,                        status: 1,                        page: this.page,                    }, this.search),                }).then(e => {                    this.listLoading = false;                    if (e.data.code === 0) {                        this.storeList = e.data.data.list;                        this.pagination = e.data.data.pagination;                    } else {                        this.$message.error(e.data.msg);                    }                });            },        },    })</script>